mapper.update:
		UPDATE tb_user 
		   SET USR_ID = #{usr_id}
		
		   	   USR_PASSWORD = #{usr_password},
		
		
    		   USR_IMAGE = #{usr_image},
		
		
    		   USR_SMS = #{usr_sms},
		
		
    	 	   USR_KAKAO = #{usr_kakao}
		
    	 WHERE USR_ID = #{usr_id}

mapper.select:
SELECT USR_ID , USR_NAME , USR_ACTIVE , USR_ACTIVE_CODE , CNT FROM ( SELECT ROWNUM AS RN , A.* FROM ( SELECT U.USR_ID , U.USR_NAME , U.USR_ACTIVE , C.CCD_CODENAME AS USR_ACTIVE_CODE , COUNT(USR_ID) OVER() CNT FROM TB_USER U, TB_COMMON_CODE C WHERE U.USR_ACTIVE = C.CCD_CODE
AND USR_ID LIKE '%'||#{keyword}||'%'
AND USR_NAME LIKE '%'||#{keyword}||'%'
) A WHERE ROWNUM <= #{end_idx} ) WHERE RN >= #{start_idx}

mapper.update:
		UPDATE TB_USER 
		   SET USR_ACTIVE = #{usr_active}
		     , USR_TYPE = #{usr_type}
    	 WHERE USR_ID = #{usr_id}
