mapper.select:
SELECT COM_NAME
  FROM (
		SELECT COM_NAME
		  FROM TB_COMMON
		 WHERE COM_CATEGORY1 = #{com_category1}
		   AND COM_CATEGORY2 = #{com_category2}
		   AND COM_CATEGORY3 = #{com_category3}
		 ORDER BY COM_NAME DESC
		)
 WHERE ROWNUM = 1

SELECT CDE_CODE
  FROM TB_COMMON_DETAIL
 WHERE CDE_ID = #{cde_id}

SELECT CDE_ID, CDE_CODE
  FROM TB_COMMON_DETAIL
 WHERE CDE_UPPERID = #{cde_upperid}

		SELECT COUNT(CDE_ID) AS CNT
		  FROM TB_COMMON_DETAIL
		 WHERE CDE_DEPTH = #{cde_depth}
           AND CDE_CODE = #{cde_code}
